import { useState, useEffect } from 'react';

import ProductItem from './ProductItem';
import classes from './Products.module.css';

// const DUMMY_PRODUCTS = [
//   {
//     id: 'p1',
//     name: 'React With JS',
//     price: 23.99,
//     description: 'The Best ReactJS Book for learning in the market.'
//   },
//   {
//     id: 'p2',
//     name: 'Node With JS',
//     price: 33.99,
//     description: 'The Best NodeJS Book for learning in the market.'
//   },
//   {
//     id: 'p3',
//     name: 'Express With JS',
//     price: 13.99,
//     description: 'The Best ExpressJS Book for learning in the market.'
//   },
// ]

const Products = (props) => {

  const [productList, setProductList] = useState([]);

  const fetchProducts = async() => {
    try {
      const response = await fetch("https://redux-store-97f56-default-rtdb.asia-southeast1.firebasedatabase.app/products.json")
      if(!response.ok) {
        throw new Error('Something Went Wrong!')
      }
      const data = await response.json();

      let fetchedProducts = [];
      for(const key in data){
        fetchProducts.push(...data[key])
      }

      setProductList(fetchedProducts)

    } catch (error) {
      console.log(error.messsage || 'oops!');
    }
  }

  useEffect(() => {
    fetchProducts();
  }, [])

  const products = productList.map(prod => (
    <ProductItem key={prod.id} {...prod}/>
  ))

  return (
    <section className={classes.products}>
      <h2>Buy your favorite products</h2>
      <ul>
        {products}
      </ul>
    </section>
  );
};

export default Products;
