import { createSlice } from "@reduxjs/toolkit";
import { uiActions } from "./uiSlice";

const initialCartState = {
    items: [],
    badgeQty: 0,
    totalAmount: 0
}

const cartSlice = createSlice({
    name: 'cart',
    initialState: initialCartState,
    reducers: {
        addItemToCart(state, action) {
            const item = action.payload;

            const itemExists = state.items.find(i => i.id === item.id);

            if(!itemExists) {
                state.items.push({
                    id: item.id,
                    name: item.name,
                    quantity: 1,
                    price: item.price,
                    totalAmount: item.price
                })
            } else {
                itemExists.quantity += 1
                itemExists.totalAmount += itemExists.price
            }

            state.badgeQty += 1
            state.totalAmount +=  item.price

        },
        removeItemFromCart(state, action) {
            const id = action.payload;

            const itemExists = state.items.find(item => item.id === id);

            if(!itemExists) {
                return
            }

            if(itemExists.quantity === 1) {
                state.items = state.items.filter(item => item.id !== id)
            } else {
                itemExists.quantity -= 1
                itemExists.totalAmount -= itemExists.price
            }

            state.badgeQty -= 1
            state.totalAmount -=  itemExists.price

        },
        clearCart(state) {
            state = initialCartState
        },
    }
})

export const sendCartData = (cart) => {
    return async(dispatch) => {
        dispatch(uiActions.showNotification({
            status: 'pending',
            title: 'Sending...',
            message: 'Sending cart data'
          }))

        const sendData = async() => {
            const response = await fetch("https://redux-store-97f56-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json", {
                method: 'PUT',
                body: JSON.stringify(cart)
            })

            if(!response.ok) {
                throw new Error('Sending data failed')
            }
        }

        try {
            await sendData()
            dispatch(uiActions.showNotification({
                status: 'success',
                title: 'Success',
                message: 'Sent cart data successfully'
              }))
        } catch (error) {
            dispatch(uiActions.showNotification({
                status: 'error',
                title: 'Error...',
                message: 'Something went wrong'
              }))
        }
    }
}

export const cartActions = cartSlice.actions

export default cartSlice.reducer
